import {
  useGetTransactionsQuery,
  useDeleteTransactionMutation,
} from "../../store/api";

import styles from "./Overview.module.css";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";
import { useState } from "react";

export default function Overview() {
  const { data: transactions = [] } = useGetTransactionsQuery();
  const [deleteTransaction] = useDeleteTransactionMutation();

  // filters state
  const [dateFilter, setDateFilter] = useState("all");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");

  // üî• –î–û–ë–ê–í–õ–ï–ù–û
  const [currencyModal, setCurrencyModal] = useState(false);
  const [selectedCurrency, setSelectedCurrency] = useState("");

  // ---- FILTERED LIST ----
  const filtered = transactions.filter((t) => {
    if (typeFilter !== "all" && t.type !== typeFilter) return false;
    if (categoryFilter !== "all" && t.category !== categoryFilter) return false;

    const tDate = new Date(t.date);
    const today = new Date();

    if (dateFilter === "day") {
      return tDate.toDateString() === today.toDateString();
    }

    if (dateFilter === "week") {
      const diff = (today - tDate) / (1000 * 60 * 60 * 24);
      return diff <= 7;
    }

    if (dateFilter === "month") {
      return (
        tDate.getMonth() === today.getMonth() &&
        tDate.getFullYear() === today.getFullYear()
      );
    }

    return true;
  });

  // ---- BALANCE LINE CHART ----
  const balanceData = transactions.map((t, i) => ({
    name: t.date,
    balance: transactions
      .slice(0, i + 1)
      .reduce(
        (sum, x) => sum + (x.type === "income" ? x.amount : -x.amount),
        0
      ),
  }));

  // ---- PIE CHART ----
  const expenses = transactions.filter((t) => t.type === "expense");

  const categories = {};
  expenses.forEach((e) => {
    categories[e.category] = (categories[e.category] || 0) + e.amount;
  });

  const pieData = Object.keys(categories).map((k) => ({
    name: k,
    value: categories[k],
  }));

  const pieColors = ["#60a5fa", "#7dd3fc", "#fb7185", "#a78bfa", "#34d399"];

  // ---- DELETE ----
  const deleteHandler = async (id) => {
    await deleteTransaction(id);
  };

  // ---- DOWNLOAD JSON ----
  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(transactions, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");

    a.href = url;
    a.download = "transactions.json";
    a.click();
  };

  // üî• –î–û–ë–ê–í–õ–ï–ù–û ‚Äî –≤—ã–±–æ—Ä –≤–∞–ª—é—Ç—ã
  const currencies = [
    "USD", "EUR", "GBP", "JPY", "CNY", "KZT", "UAH", "BYN", "TRY", "CHF",
    "AED", "AUD", "CAD", "SEK", "NOK", "PLN", "INR", "KRW", "BRL", "HKD",
    "SGD", "DKK", "ZAR", "MXN", "HUF", "CZK", "NZD", "RSD", "AMD", "GEL"
  ];

  const convertCurrency = async () => {
    if (!selectedCurrency) return;

    // –¢–´ –í–°–¢–ê–í–ò–®–¨ –°–Æ–î–ê API
    console.log("–ö–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏—è –≤:", selectedCurrency);

    setCurrencyModal(false);
  };

  return (
    <div className={styles.page}>
      <h1 className={styles.title}>Overview</h1>

      {/* GRAPHS */}
      <div className={styles.grid}>
        <div className={styles.card}>
          <h3 className={styles.cardTitle}>–ë–∞–ª–∞–Ω—Å</h3>
          <ResponsiveContainer width="100%" height={260}>
            <LineChart data={balanceData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="balance" stroke="#60a5fa" strokeWidth={3} />
