          </ResponsiveContainer>
        </div>
      </div>

      {/* FILTERS */}
      <div className={styles.filters}>
        <select onChange={(e) => setDateFilter(e.target.value)}>
          <option value="all">–í—Å—è –∏—Å—Ç–æ—Ä–∏—è</option>
          <option value="day">–°–µ–≥–æ–¥–Ω—è</option>
          <option value="week">–ù–µ–¥–µ–ª—è</option>
          <option value="month">–ú–µ—Å—è—Ü</option>
        </select>

        <select onChange={(e) => setCategoryFilter(e.target.value)}>
          <option value="all">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</option>
          <option value="–ï–¥–∞">–ï–¥–∞</option>
          <option value="–¢—Ä–∞–Ω—Å–ø–æ—Ä—Ç">–¢—Ä–∞–Ω—Å–ø–æ—Ä—Ç</option>
          <option value="–†–∞–∑–≤–ª–µ—á–µ–Ω–∏—è">–†–∞–∑–≤–ª–µ—á–µ–Ω–∏—è</option>
          <option value="–ü–æ–∫—É–ø–∫–∏">–ü–æ–∫—É–ø–∫–∏</option>
          <option value="–î–æ—Ö–æ–¥">–î–æ—Ö–æ–¥</option>
        </select>

        <select onChange={(e) => setTypeFilter(e.target.value)}>
          <option value="all">–¢–∏–ø</option>
          <option value="income">–î–æ—Ö–æ–¥</option>
          <option value="expense">–†–∞—Å—Ö–æ–¥</option>
        </select>

        <button className={styles.downloadBtn} onClick={downloadJSON}>
          –°–∫–∞—á–∞—Ç—å JSON
        </button>

        {/* üî• –ù–û–í–ê–Ø –ö–ù–û–ü–ö–ê */}
        <button
          className={styles.currencyBtn}
          onClick={() => setCurrencyModal(true)}
        >
          –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –≤–∞–ª—é—Ç—É
        </button>
      </div>

      {/* üî• –ú–û–î–ê–õ–¨–ù–û–ï –û–ö–ù–û */}
      {currencyModal && (
        <div className={styles.modalBg}>
          <div className={styles.modal}>
            <h3>–í—ã–±–µ—Ä–∏—Ç–µ –≤–∞–ª—é—Ç—É</h3>

            <select
              className={styles.currencySelect}
              value={selectedCurrency}
              onChange={(e) => setSelectedCurrency(e.target.value)}
            >
              <option value="">–í—ã–±–µ—Ä–∏—Ç–µ...</option>
              {currencies.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>

            <div className={styles.modalActions}>
              <button className={styles.cancelBtn} onClick={() => setCurrencyModal(false)}>
                –û—Ç–º–µ–Ω–∞
              </button>

              <button className={styles.applyBtn} onClick={convertCurrency}>
                –ü—Ä–∏–º–µ–Ω–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}

      {/* TABLE */}
      <div className={styles.card}>
        <table className={styles.table}>
          <thead>
            <tr>
              <th>–î–∞—Ç–∞</th>
              <th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
              <th>–¢–∏–ø</th>
              <th>–°—É–º–º–∞</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {filtered.map((t) => (
              <tr key={t._id}>
                <td>{t.date}</td>
                <td>{t.category}</td>
                <td className={t.type === "income" ? styles.income : styles.expense}>
                  {t.type}
                </td>
                <td>{t.amount} ‚ÇΩ</td>

                <td>
                  <button
                    className={styles.deleteBtn}
                    onClick={() => deleteHandler(t._id)}
                  >
                    ‚úï
                  </button>
                </td>
