import React, { useState } from "react";
import styles from "./Overview.module.css";
import { useSelector, useDispatch } from "react-redux";
import { addTransaction } from "../../store/transactionsSlice";

const Overview = () => {
  const dispatch = useDispatch();
  const transactions = useSelector((state) => state.transactions.list);

  // ФИЛЬТРЫ
  const [typeFilter, setTypeFilter] = useState("all");
  const [categoryFilter, setCategoryFilter] = useState("all");

  // Расчёт баланса
  const balance = transactions.reduce((acc, t) => {
    return t.type === "income" ? acc + t.amount : acc - t.amount;
  }, 0);

  // Применение фильтров
  const filtered = transactions.filter((t) => {
    if (typeFilter !== "all" && t.type !== typeFilter) return false;
    if (categoryFilter !== "all" && t.category !== categoryFilter) return false;
    return true;
  });

  // Скачать JSON
  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(transactions, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = "transactions.json";
    link.click();
  };

  return (
    <div className={styles.container}>
      <h1>Обзор</h1>

      {/* Блок баланса */}
      <div className={styles.balanceBox}>
        <h2>Баланс</h2>
        <p className={balance >= 0 ? styles.green : styles.red}>
          {balance} ₽
        </p>
      </div>

      {/* Фильтры */}
      <div className={styles.filters}>
        <select value={typeFilter} onChange={(e) => setTypeFilter(e.target.value)}>
          <option value="all">Все типы</option>
          <option value="income">Доходы</option>
          <option value="expense">Расходы</option>
        </select>

        <select
          value={categoryFilter}
          onChange={(e) => setCategoryFilter(e.target.value)}
        >
          <option value="all">Все категории</option>
          <option value="Еда">Еда</option>
          <option value="Транспорт">Транспорт</option>
          <option value="Развлечения">Развлечения</option>
          <option value="Покупки">Покупки</option>
          <option value="Другое">Другое</option>
          <option value="Доход">Доход</option>
        </select>

        <button className={styles.jsonBtn} onClick={downloadJSON}>
          Скачать JSON
        </button>
      </div>

      {/* Список транзакций */}
      <div className={styles.list}>
        {filtered.length === 0 && <p>Нет операций</p>}

        {filtered.map((t) => (
          <div key={t.id} className={styles.item}>
            <div>
              <p className={styles.title}>
                {t.type === "income" ? "Доход" : t.category}
              </p>
              <p className={styles.comment}>{t.comment}</p>
              <p className={styles.date}>{t.date}</p>
            </div>

            <div className={t.type === "income" ? styles.green : styles.red}>
              {t.type === "income" ? "+" : "-"} {t.amount} ₽
            </div>

            <button
              className={styles.deleteBtn}
              onClick={() => dispatch(removeTransaction(t.id))}
            >
              ✕
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Overview;
