import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { deleteTransaction } from "../../store/transactionsSlice";

import styles from "./Overview.module.css";

import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";

export default function Overview() {
  const transactions = useSelector((state) => state.transactions.list);
  const dispatch = useDispatch();

  // ---------- FILTERS ----------
  const [dateFilter, setDateFilter] = useState("all");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");

  // ---------- BALANCE LINE CHART ----------
  const balanceData = transactions.map((t, idx) => ({
    name: t.date,
    balance: transactions
      .slice(0, idx + 1)
      .reduce(
        (total, item) =>
          total + (item.type === "income" ? item.amount : -item.amount),
        0
      ),
  }));

  // ---------- PIE CHART (EXPENSES) ----------
  const expenses = transactions.filter((t) => t.type === "expense");

  const categories = {};
  expenses.forEach((e) => {
    categories[e.category] = (categories[e.category] || 0) + e.amount;
  });

  const pieData = Object.keys(categories).map((key) => ({
    name: key,
    value: categories[key],
  }));

  const pieColors = ["#60a5fa", "#7dd3fc", "#fb7185", "#a78bfa", "#34d399"];

  // ---------- FILTERING ----------
  const filtered = transactions.filter((t) => {
    if (typeFilter !== "all" && t.type !== typeFilter) return false;
    if (categoryFilter !== "all" && t.category !== categoryFilter) return false;

    if (dateFilter !== "all") {
      const today = new Date();
      const tDate = new Date(t.date);

      if (dateFilter === "day") {
        return tDate.toDateString() === today.toDateString();
      }

      if (dateFilter === "week") {
        const diff = (today - tDate) / (1000 * 60 * 60 * 24);
        return diff <= 7;
      }

      if (dateFilter === "month") {
        return (
          tDate.getMonth() === today.getMonth() &&
          tDate.getFullYear() === today.getFullYear()
        );
      }
    }

    return true;
  });

  // ---------- DELETE ----------
  const deleteTransactionHandler = (id) => {
    dispatch(deleteTransaction(id));
  };

  // ---------- DOWNLOAD JSON ----------
  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(transactions, null, 2)], {
      type: "application/json",
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");

    a.href = url;
    a.download = "transactions.json";
    a.click();

    URL.revokeObjectURL(url);
  };

  return (
    <div className={styles.page}>
      <h1 className={styles.title}>Overview</h1>

      {/* ---------- CHARTS ---------- */}
      <div className={styles.grid}>
        <div className={styles.card}>
          <h3 className={styles.cardTitle}>Баланс</h3>
          <ResponsiveContainer width="100%" height={250}>
            <LineChart data={balanceData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Line
                type="monotone"
                dataKey="balance"
                stroke="#60a5fa"
                strokeWidth={3}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>

        <div className={styles.card}>
          <h3 className={styles.cardTitle}>Расходы по категориям</h3>

          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie data={pieData} dataKey="value" outerRadius={90} label>
                {pieData.map((_, i) => (
                  <Cell key={i} fill={pieColors[i % pieColors.length]} />
                ))}
              </Pie>
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* ---------- FILTERS ---------- */}
      <div className={styles.filters}>
        <select onChange={(e) => setDateFilter(e.target.value)}>
          <option value="all">Вся история</option>
          <option value="day">Сегодня</option>
          <option value="week">Неделя</option>
          <option value="month">Месяц</option>
        </select>

        <select onChange={(e) => setCategoryFilter(e.target.value)}>
          <option value="all">Все категории</option>
          <option value="Еда">Еда</option>
          <option value="Транспорт">Транспорт</option>
          <option value="Развлечения">Развлечения</option>
          <option value="Покупки">Покупки</option>
          <option value="Зарплата">Зарплата</option>
        </select>

        <select onChange={(e) => setTypeFilter(e.target.value)}>
          <option value="all">Тип</option>
          <option value="income">Доход</option>
          <option value="expense">Расход</option>
        </select>

        <button className={styles.downloadBtn} onClick={downloadJSON}>
          Скачать JSON
        </button>
      </div>

      {/* ---------- TABLE ---------- */}
      <div className={styles.card}>
        <table className={styles.table}>
          <thead>
            <tr>
              <th>Дата</th>
              <th>Категория</th>
              <th>Тип</th>
              <th>Сумма</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {filtered.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", opacity: 0.6 }}>
                  Нет транзакций
                </td>
              </tr>
            ) : (
              filtered.map((t) => (
                <tr key={t.id}>
                  <td>{t.date}</td>
                  <td>{t.category}</td>
                  <td
                    className={
                      t.type === "income" ? styles.income : styles.expense
                    }
                  >
                    {t.type}
                  </td>
                  <td>{t.amount} ₽</td>
                  <td>
                    <button
                      className={styles.deleteBtn}
                      onClick={() => deleteTransactionHandler(t.id)}
                    >
                      ✕
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
