import React, { useEffect, useRef } from "react";
import styles from "./NotFound.module.css";
import { gsap } from "gsap";
import { SplitText } from "gsap/SplitText";

gsap.registerPlugin(SplitText);

const NotFoundPage = () => {
  const copyRef = useRef(null);
  const handleRef = useRef(null);
  const replayRef = useRef(null);

  useEffect(() => {
    const copyElement = copyRef.current;
    const handleElement = handleRef.current;

    const split = new SplitText(copyElement.querySelector("p"), {
      type: "chars"
    });

    const tl = gsap.timeline();
    const handleTl = gsap.timeline({ repeat: -1, yoyo: true });

    // появление букв
    tl.from(split.chars, {
      opacity: 0,
      duration: 0.05,
      stagger: 0.03,
      ease: "back.out(1.7)",
      onComplete: animateHandle
    });

    // мигание ручки
    handleTl.fromTo(
      handleElement,
      { opacity: 0 },
      { opacity: 1, duration: 0.4 }
    );

    function animateHandle() {
      const width = copyElement.querySelector("p").offsetWidth;

      gsap.to(handleElement, {
        x: width,
        duration: 0.8,
        ease: "steps(12)"
      });
    }

    // перезапуск по клику
    replayRef.current.addEventListener("click", () => {
      tl.restart();
      handleTl.restart();
    });

    return () => {
      split.revert();
    };
  }, []);

  return (
    <div className={styles.container}>
      <div ref={copyRef} className={`${styles.copyContainer} ${styles.center}`}>
        <p>404 – Page Not Found</p>
        <span ref={handleRef} className={styles.handle}></span>
      </div>

      <svg
        ref={replayRef}
        className={styles.replay}
        version="1.1"
        viewBox="0 0 279.9 297.3"
      >
      </svg>
    </div>
  );
};

export default NotFoundPage;
