import React, { useState } from "react";
import styles from "./Transaction.module.css";
import { useDispatch } from "react-redux";
import { addTransaction } from "../store/transactionsSlice";

const categories = ["Еда", "Транспорт", "Развлечения", "Покупки", "Другое"];

const Transaction = () => {
  const dispatch = useDispatch();

  const [type, setType] = useState("income");
  const [category, setCategory] = useState("Еда");
  const [amount, setAmount] = useState("");
  const [comment, setComment] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!amount || !date) return alert("Введите сумму и дату");

    const newTransaction = {
      id: Date.now(),
      type,
      category: type === "income" ? "Доход" : category,
      amount: Number(amount),
      comment,
      date
    };

    dispatch(addTransaction(newTransaction));

    // Очищаем форму
    setAmount("");
    setComment("");
    setDate("");
  };

  return (
    <div className={styles.container}>
      <h1>Создать транзакцию</h1>

      <form className={styles.form} onSubmit={handleSubmit}>
        
        <label>Тип операции</label>
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="income">Доход</option>
          <option value="expense">Расход</option>
        </select>

        {/* Показываем категории только если расход */}
        {type === "expense" && (
          <>
            <label>Категория</label>
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
              {categories.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </>
        )}

        <label>Сумма</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />

        <label>Комментарий (необязательно)</label>
        <input
          type="text"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />

        <label>Дата</label>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />

        <button className={styles.btn}>Добавить</button>
      </form>
    </div>
  );
};

export default Transaction;
