import React, { useState } from "react";
import styles from "./Transaction.module.css";

const expenseTargets = ["Еда", "Транспорт", "Развлечения", "Покупки", "Другое"];

const Transaction = () => {
  const [type, setType] = useState("income");
  const [expenseTarget, setExpenseTarget] = useState("");
  const [amount, setAmount] = useState("");
  const [comment, setComment] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const newTransaction = {
      type,
      category: type === "expense" ? expenseTarget : "Доход",
      amount: Number(amount),
      comment,
      date,
    };

    console.log("Создана транзакция:", newTransaction);
    alert("Транзакция создана!");

    setAmount("");
    setComment("");
    setDate("");
    setExpenseTarget("");
    setType("income");
  };

  return (
    <div className={`container ${styles.page}`}>
      <h2 className={styles.title}>Создать транзакцию</h2>

      <form className={styles.form} onSubmit={handleSubmit}>

        {/* Тип транзакции */}
        <div className={styles.group}>
          <label>Тип:</label>
          <select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="income">Доход</option>
            <option value="expense">Расход</option>
          </select>
        </div>

        {/* Появляется только если расход */}
        {type === "expense" && (
          <div className={styles.group}>
            <label>На что потрачено:</label>
            <select
              value={expenseTarget}
              onChange={(e) => setExpenseTarget(e.target.value)}
              required
            >
              <option value="">Выберите категорию</option>
              {expenseTargets.map((c) => (
                <option key={c} value={c}>
                  {c}
                </option>
              ))}
            </select>
          </div>
        )}

        {/* Сумма */}
        <div className={styles.group}>
          <label>Сумма:</label>
          <input
            type="number"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>

        {/* Комментарий */}
        <div className={styles.group}>
          <label>Комментарий:</label>
          <input
            type="text"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Описание"
          />
        </div>

        {/* Дата */}
        <div className={styles.group}>
          <label>Дата:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button className={styles.btn} type="submit">
          Создать
        </button>
      </form>
    </div>
  );
};

export default Transaction;
