import React, { useEffect, useRef } from "react";
import styles from "./NotFound.module.css";
import { gsap } from "gsap";
import { SplitText } from "gsap/SplitText";

gsap.registerPlugin(SplitText);

const NotFoundPage = () => {
  const copyRef = useRef(null);
  const handleRef = useRef(null);
  const replayRef = useRef(null);

  useEffect(() => {
    const copyElement = copyRef.current;
    const handleElement = handleRef.current;

    const split = new SplitText(copyElement.querySelector("p"), {
      type: "chars"
    });

    const tl = gsap.timeline();
    const handleTl = gsap.timeline({ repeat: 1, yoyo: true });

    // появление букв
    tl.from(split.chars, {
      opacity: 0,
      duration: 0.05,
      stagger: 0.03,
      ease: "back.out(1.7)",
      onComplete: animateHandle
    });

    // мигание ручки
    handleTl.fromTo(
      handleElement,
      { opacity: 0 },
      { opacity: 1, duration: 0.4 }
    );

    function animateHandle() {
      const width = copyElement.querySelector("p").offsetWidth;

      gsap.to(handleElement, {
        x: width,
        duration: 0.8,
        ease: "steps(12)"
      });
    }

    // перезапуск по клику
    replayRef.current.addEventListener("click", () => {
      tl.restart();
      handleTl.restart();
    });

    return () => {
      split.revert();
    };
  }, []);

  return (
    <div className={styles.container}>
      <div ref={copyRef} className={`${styles.copyContainer} ${styles.center}`}>
        <p>404 – Page Not Found</p>
        <span ref={handleRef} className={styles.handle}></span>
      </div>

      <svg
        ref={replayRef}
        className={styles.replay}
        version="1.1"
        viewBox="0 0 279.9 297.3"
      >
        <g>
          <path d="M269.4,162.6c-2.7,66.5-55.6,120.1-121.8,123.9c-77,4.4-141.3-60-136.8-136.9C14.7,81.7,71,27.8,140,27.8
            c1.8,0,3.5,0,5.3,0.1c0.3,0,0.5,0.2,0.5,0.5v15c0,1.5,1.6,2.4,2.9,1.7l35.9-20.7c1.3-0.7,1.3-2.6,0-3.3L148.6,0.3
            c-1.3-0.7-2.9,0.2-2.9,1.7v15c0,0.3-0.2,0.5-0.5,0.5c-1.7-0.1-3.5-0.1-5.2-0.1C63.3,17.3,1,78.9,0,155.4
            C-1,233.8,63.4,298.3,141.9,297.3c74.6-1,135.1-60.2,138-134.3c0.1-3-2.3-5.4-5.3-5.4l0,0C271.8,157.6,269.5,159.8,269.4,162.6z"/>
        </g>
      </svg>
    </div>
  );
};

export default NotFoundPage;
