import React, { useMemo } from "react";
import { useSelector, useDispatch } from "react-redux";
import { LineChart, Line, PieChart, Pie, Cell, Tooltip, ResponsiveContainer } from "recharts";
import styles from "./Overview.module.css";
import { removeTransaction } from "../../store/transactionsSlice";

const COLORS = ["#4ade80", "#60a5fa", "#f472b6", "#facc15", "#fb7185"];

export default function Overview() {
  const dispatch = useDispatch();
  const transactions = useSelector((state) => state.transactions.list);

  const [filterType, setFilterType] = React.useState("all");
  const [filterCategory, setFilterCategory] = React.useState("all");

  // ---- Фильтр ----
  const filtered = useMemo(() => {
    return transactions.filter((t) => {
      if (filterType !== "all" && t.type !== filterType) return false;
      if (filterCategory !== "all" && t.category !== filterCategory) return false;
      return true;
    });
  }, [transactions, filterType, filterCategory]);

  // ---- Линейный график: динамика баланса ----
  const lineData = useMemo(() => {
    let balance = 0;
    return transactions.map((t) => {
      balance += t.type === "income" ? t.amount : -t.amount;
      return { date: t.date, balance };
    });
  }, [transactions]);

  // ---- Круговая диаграмма: расходы по категориям ----
  const pieData = useMemo(() => {
    const map = {};

    transactions.forEach((t) => {
      if (t.type === "expense") {
        if (!map[t.category]) map[t.category] = 0;
        map[t.category] += t.amount;
      }
    });

    return Object.entries(map).map(([category, value]) => ({
      name: category,
      value,
    }));
  }, [transactions]);

  // ---- Выгрузка JSON ----
  const downloadJSON = () => {
    const file = new Blob([JSON.stringify(transactions, null, 2)], {
      type: "application/json",
    });

    const url = URL.createObjectURL(file);
    const a = document.createElement("a");
    a.href = url;
    a.download = "transactions.json";
    a.click();
  };

  return (
    <div className={styles.container}>
      <h1>Overview</h1>

      {/* ---- График баланса ---- */}
      <div className={styles.block}>
        <h2>Balance Over Time</h2>
        <div className={styles.chartBox}>
          <ResponsiveContainer width="100%" height={250}>
            <LineChart data={lineData}>
              <Tooltip />
              <Line type="monotone" dataKey="balance" stroke="#3b82f6" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* ---- Круговой график ---- */}
      <div className={styles.block}>
        <h2>Expenses Breakdown</h2>
        <div className={styles.pieBox}>
          <ResponsiveContainer width="100%" height={260}>
            <PieChart>
              <Tooltip />
              <Pie
                data={pieData}
                dataKey="value"
                nameKey="name"
                outerRadius={110}
                innerRadius={60}
                paddingAngle={5}
              >
                {pieData.map((_, i) => (
                  <Cell key={i} fill={COLORS[i % COLORS.length]} />
                ))}
              </Pie>
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* ---- Фильтры ---- */}
      <div className={styles.filters}>
        <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
          <option value="all">Все типы</option>
          <option value="income">Доход</option>
          <option value="expense">Расход</option>
        </select>

        <select value={filterCategory} onChange={(e) => setFilterCategory(e.target.value)}>
          <option value="all">Все категории</option>
          <option value="Еда">Еда</option>
          <option value="Транспорт">Транспорт</option>
          <option value="Развлечения">Развлечения</option>
          <option value="Прочее">Прочее</option>
        </select>

        <button className={styles.jsonBtn} onClick={downloadJSON}>
          Скачать JSON
        </button>
      </div>

      {/* ---- Список операций ---- */}
      <div className={styles.list}>
        {filtered.map((t) => (
          <div className={styles.item} key={t.id}>
            <div>
              <div className={styles.title}>{t.type === "income" ? "Доход" : "Расход"}</div>
              <div className={styles.comment}>{t.comment}</div>
              <div className={styles.date}>{t.date}</div>
            </div>

            <div style={{ fontWeight: 700, color: t.type === "income" ? "#4ade80" : "#f87171" }}>
              {t.amount} ₽
            </div>

            <button className={styles.deleteBtn} onClick={() => dispatch(removeTransaction(t.id))}>
              ✖
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
